const { longestPalindrome } = require("./longestPalindrome.mjs");

describe("Custom", () => {
  test("minimal requirements", () => {
    const result = longestPalindrome("ab");
    expect(result).toBe(1);
  });
});

describe("Official", () => {
  test("Example 1", () => {
    const result = longestPalindrome("abccccdd");
    expect(result).toBe(7);
  });
  test("Example 2", () => {
    const result = longestPalindrome("a");
    expect(result).toBe(1);
  });

  test("Testcase 48", () => {
    const result = longestPalindrome("bb");
    expect(result).toBe(2);
  });
  test("Testcase 49", () => {
    const result = longestPalindrome("");
    expect(result).toBe(983);
  });
  test("Testcase 65", () => {
    const result = longestPalindrome("");
    expect(result).toBe(445);
  });
});
